    form1() {
      text("Images example");
      client_size({430, 480});
      controls().push_back_range({choice_size, picture, label_picture_name, button_previous, button_next});

      choice_size.bounds({75, 25, 280, 25});
      choice_size.items().push_back_range({"16 x 16 pixels", "24 x 24 pixels", "32 x 32 pixels", "48 x 48 pixels", "64 x 64 pixels", "128 x 128 pixels", "256 x 256 pixels"});
      choice_size.selected_index_changed += [&] {
        picture.image(pictures[choice_size.selected_index()][current_image_index]);
      };
      choice_size.selected_index(6);
      
      picture.back_color(system_colors::window());
      picture.border_style(forms::border_style::fixed_3d);
      picture.bounds({75, 75, 280, 280});
      picture.size_mode(picture_box_size_mode::center_image);

      label_picture_name.text_align(content_alignment::middle_center);
      label_picture_name.bounds({75, 370, 280, label_picture_name.height()});
      label_picture_name.text(picture_names[current_image_index]);

      button_previous.auto_repeat(true);
      button_previous.enabled(false);
      button_previous.image(images::previous_32x32());
      button_previous.image_align(content_alignment::middle_left);
      button_previous.text("&Previous");
      button_previous.bounds({75, 410, 125, 40});
      button_previous.click += [&] {
        if (current_image_index > 0) picture.image(pictures[choice_size.selected_index()][--current_image_index]);
        label_picture_name.text(picture_names[current_image_index]);
        button_previous.enabled(current_image_index > 0);
        button_next.enabled(current_image_index < pictures[choice_size.selected_index()].size() - 1);
      };
      
      button_next.auto_repeat(true);
      button_next.image(images::next_32x32());
      button_next.image_align(content_alignment::middle_right);
      button_next.text("&Next");
      button_next.bounds({230, 410, 125, 40});
      button_next.click += [&] {
        if (current_image_index < pictures[choice_size.selected_index()].size()) picture.image(pictures[choice_size.selected_index()][++current_image_index]);
        label_picture_name.text(picture_names[current_image_index]);
        button_previous.enabled(current_image_index > 0);
        button_next.enabled(current_image_index < pictures[choice_size.selected_index()].size() - 1);
      };
    }
    
